mapper.select:
select
  u_id, u_name, u_account, u_pwd, u_time, u_sid
from db_personnelmanagement.t_user


        and u_id = #{uId}


        and u_name = #{uName}


        and u_account = #{uAccount}


        and u_pwd = #{uPwd}


        and u_time = #{uTime}


        and u_sid = #{uSid}

select
  u_id, u_name, u_account, u_pwd, u_time, u_sid
from db_personnelmanagement.t_user
where u_account = #{uAccount}
and u_pwd = #{uPwd}
